    private ListView listViewFlickr;

    @Override
    public void onCreate(Bundle savedInstanceState) {
        Log.e(TAG, "create");
        super.onCreate(savedInstanceState);
        setContentView(R.layout.flickr_test);
        List<String> flickrImgUrls = null;
        try {
            flickrImgUrls = parse(flickrDownloadedJson);
            Log.e(TAG, flickrImgUrls.toString());
        } catch (JSONException e) {
            Log.e(TAG, e.getMessage());
        }
        listViewFlickr = (ListView) findViewById(R.id.list_view);
        FlickrListAdapter flkrAdapter = new FlickrListAdapter(this, R.layout.image_item, flickrImgUrls);
        listViewFlickr.setAdapter(flkrAdapter);


    }

    private List<String> parse(String flickrDownloadedJson) throws JSONException {
        Log.d(TAG, "start");
        JSONObject body = new JSONObject(flickrDownloadedJson).getJSONObject("photos");
        JSONArray flickrImgsArray = body.getJSONArray("photo");
        List<String> flickrImageUrls = new ArrayList<String>(flickrDownloadedJson.length());
        for (int count = 0; count < flickrImgsArray.length() ; count ++)
        {
            JSONObject photo = (JSONObject) flickrImgsArray.get(count);
            Log.d(TAG, photo.toString());
            flickrImageUrls.add(photo.optString("url_n"));
        }
        return flickrImageUrls;
    }

